import collections

class Solution:
    def minWindow(self, s: str, t: str) -> str:
        if not s or not t:
            return ""

        idx = []

        required = collections.Counter(t)
        rlen = len(required)
        count = collections.Counter()

        def match_count(required, count):
            if rlen > len(list(count)):
                return False
            for k in required.keys():
                u = required[k]
                v = count[k]
                if v < u:
                    return False
            return True

        res = None
        start = 0
        for i in range(len(s)):
            c = s[i]
            if c in required:
                idx.append(i)
                count[c] = count[c] + 1 if c in count else 1

                # i is at the end, now move start
                while match_count(required, count):
                    u = s[idx[start]: i+1]
                    res = res if res is not None and len(res) <= len(u) else u

                    removed = s[idx[start]]
                    count[removed] = count[removed] - 1
                    start += 1

        if res is None:
            res = ""
        return res

print(Solution().minWindow('ADOBECODEBANC', 'ABC'))  # "BANC"
print(Solution().minWindow('a', 'aa'))  # ""
print(Solution().minWindow('aa', 'aaa'))  # ""
print(Solution().minWindow('bbaac', 'aba'))  # baa
print(Solution().minWindow('cabwefgewcwaefgcf', 'cae'))  # cwae

a = ""
b = ""
print(Solution().minWindow(a, b))
